mapper.select:
SELECT * FROM MEMBER
WHERE MEM_ID LIKE #{memId}
AND MEM_PASS LIKE #{memPass}
AND MEM_AUTH != 0

SELECT * FROM BOARD
WHERE BO_NO = #{boNo}
AND BO_TYPE = #{boType}

mapper.update:
UPDATE BOARD 
SET BO_TITLE = #{boTitle}, 
BO_CONTENT = #{boContent},
BO_REGDATE = SYSDATE
WHERE BO_NO = #{boNo}
AND BO_TYPE = #{boType}

mapper.delete:
DELETE 
FROM BOARD
WHERE BO_NO = #{boNo}
AND BO_TYPE = #{boType}

mapper.select:
SELECT * FROM MEMBER
WHERE MEM_AUTH != 0
AND MEM_ID = #{memId}

mapper.insert:
INSERT INTO 
MEMBER (MEM_ID, MEM_PASS, MEM_NAME, MEM_MAIL) 
VALUES(
	#{memId}, #{memPass}, #{memName}, #{memMail}
)
